import React, {useState} from 'react';
import {useDynamicList, useForm, useList} from '@shopify/react-form';
import logo from './logo.svg';
import {TextField, AppProvider, Button, Form, FormLayout, PageActions, Page} from '@shopify/polaris'
import '@shopify/polaris/dist/styles.css';

interface Card {
  cardNumber: string;
  cvv: string
}
function App() {

  const emptyCard = (): Card => ({
    cardNumber: '',
    cvv: '',
  })
  const {fields, addField, removeField} = useDynamicList([{cardNumber: '4234 6738 8920 8902', cvv: '422'}], emptyCard);
  const {submit, dirty, submitting} = useForm({
    fields: {
      list: fields
    },
    onSubmit: async fieldValues => {
      console.log(fieldValues);

      return {status: 'success'};
    }
  })
  return (
      <AppProvider i18n={{}}>
      <Page title="Customer Payment Info">
        <Form onSubmit={submit}>
          <FormLayout>
            {fields.map((field, index) => (
                    <FormLayout.Group key={index}>
                      <TextField placeholder="Card Number" value={field.cardNumber.value} onChange={field.cardNumber.onChange}/>
                      <TextField placeholder="CVV" value={field.cvv.value} onChange={field.cvv.onChange} key={index}/>
                      <Button onClick={() => removeField(index)}>Remove</Button>
                    </FormLayout.Group>
            ))}
            <Button onClick={() => addField()}>Add Card</Button>
          </FormLayout>
          <PageActions
            primaryAction={{
              content: 'Save',
              disabled: !dirty,
              loading: submitting,
              accessibilityLabel: 'Save',
              onAction: submit,
            }}
            secondaryActions={[
              {
                content: 'Delete',
                destructive: true,
                onAction: () => {},
                accessibilityLabel: 'Delete',
              },
            ]}
          />
        </Form>
      </Page>
      </AppProvider>
  );
}

export default App;
